function calculateNOIMargin(data) {
  const noi = data.noi || 0;
  const revenue = data.totalRevenue || 1;
  return ((noi / revenue) * 100).toFixed(2) + '%';
}

function calculateDSCR(data) {
  const noi = data.noi || 0;
  const debtService = data.monthlyDebtService * 12 || 1;
  return (noi / debtService).toFixed(2);
}

function calculateCapRate(data) {
  const noi = data.noi || 0;
  const propertyValue = data.propertyValue || 1;
  return ((noi / propertyValue) * 100).toFixed(2) + '%';
}

function calculateCashOnCash(data) {
  const cashFlow = data.noi - (data.monthlyDebtService * 12);
  const equityInvested = data.propertyValue * 0.25;
  return ((cashFlow / equityInvested) * 100).toFixed(2) + '%';
}

function calculateEffectiveOccupancy(data) {
  const collected = data.collectedRent || 0;
  const potential = data.grossPotentialRent || 1;
  return ((collected / potential) * 100).toFixed(2) + '%';
}

function calculateLeveredIRR(cashFlows) {
  // Newton-Raphson IRR solver
  let irr = 0.1;
  for (let i = 0; i < 100; i++) {
    let npv = 0, dnpv = 0;
    cashFlows.forEach((cf, t) => {
      npv += cf / Math.pow(1 + irr, t);
      dnpv -= t * cf / Math.pow(1 + irr, t + 1);
    });
    irr -= npv / dnpv;
  }
  return (irr * 100).toFixed(2) + '%';
}

function calculateEquityMultiple(data) {
  const totalDistributions = data.distributions.reduce((a,b) => a+b, 0);
  const equity = data.equityInvested || 1;
  return (totalDistributions / equity).toFixed(2) + 'x';
}

function calculateRentGrowth(currentRent, priorRent) {
  return (((currentRent - priorRent) / priorRent) * 100).toFixed(2) + '%';
}

function calculateLossToLease(data) {
  const loss = data.marketRent - data.inPlaceRent;
  const pct = (loss / data.marketRent) * 100;
  return { amount: '$' + loss.toFixed(0), percent: pct.toFixed(1) + '%' };
}

function calculateBreakEven(data) {
  const totalCosts = data.opex + data.debtService;
  const gpr = data.grossPotentialRent;
  return ((totalCosts / gpr) * 100).toFixed(1) + '%';
}
